import React, {useState,useEffect} from "react";
import Grid from '@mui/material/Grid';
import { Container } from "@mui/system";
import { Input,InputLabel } from "@mui/material";
import Box from '@mui/material/Box';
import FormControl from '@mui/material/FormControl';
import Button from '@mui/material/Button';
import UserList from './Components/UserList';
import UserEdit from './Components/UserEdit';
import Axios from 'axios';
import {BrowserRouter as Router, Routes, Route} from "react-router-dom";

const App = ()=> {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [position, setPosition] = useState("");

  const [userList,setUserList] = useState([]);

  useEffect(() => {
    setTimeout(()=>{
      UsersAPI()
    },100)
  },[]);

  const UsersAPI = () =>{
    Axios.get(`http://localhost:8000/`)
      .then(res => {
          const users = res.data;
          setUserList( users );
          console.log(userList)
      }).catch(err =>{
        console.log(err)
      });
  }


  const addUser = () => {
    const user = { name: name, age: age, position: position };
    Axios.post('http://localhost:8000/addUser', user)
      .then(res => {
        setUserList({ 
          ...userList,
          id: res.data.id ,
          name: res.data.name,
          age: res.data.age,
          position: res.data.position
        })
        console.log()
        setName("")
        setAge("")
        setPosition("")
      }).catch(err=>{
        console.log(err)
      });
  }

  const deleteUser = (id) => {
    Axios.delete(`http://localhost:8000/deleteUser/${id}`)
      .then(() => {
        let user = [...userList]
        user.splice(id,1)
        setUserList(user)
    }).catch(err=>{
        console.log(err)
    });
  }

  const center = {
    alignItem: "center",
    textAlign: "center"
  }

  return (
    <Router>
      <div className="App">
        <Container>

          <Routes>
            <Route path="EditUser/:id" element={<UserEdit  item={userList} />}  ></Route>
            <Route path="/" index element={<UserEdit  item={userList} />}></Route>
          </Routes>
        </Container>
      </div>
    </Router>
  );
}

export default App;
