import React,{useEffect,useState} from "react";
import {useParams} from "react-router-dom";
import Grid from '@mui/material/Grid';
import { Container } from "@mui/system";
import { Input,InputLabel } from "@mui/material";
import Box from '@mui/material/Box';
import FormControl from '@mui/material/FormControl';
import Button from '@mui/material/Button';
import Axios from 'axios';

const UserEdit = (props) => {
    const [name, setName] = useState("");
    const [age, setAge] = useState("");
    const [position, setPosition] = useState("");

    const [user, setUser] = useState([]);

    const {id} = useParams();

    console.log(id)
    console.log(props.item)

    useEffect(() => {
        setTimeout(()=>{
            getUser()
        console.log(user)

        },100)
    },[]);

    const getUser = () =>{
        setUser(props.item.filter(res => res.id === id))
    }

    const editUser = (id) => {
        const user = { name: name, age: age, position: position };
        Axios.put(`http://localhost:8000/editUser/${id}`, user)
        .then(() => {
            console.log("edit success")
        }).catch(err=>{
            console.log(err)
        });
    }

    const center = {
        alignItem: "center",
        textAlign: "center"
    }

    return(
        <Container>
          <Grid container >
            <Grid item xs={12} md={12}  style={center} >
              <Box
                component="form"
                noValidate
                autoComplete="off"
                className="box"
              >
                <h1>Edit User</h1>
                <FormControl fullWidth variant="standard" >
                  <InputLabel htmlFor="component-simple">Name</InputLabel>
                  <Input type="text" placeholder="Name" id="name" value={user.name}  onChange={(e) => {setName(e.target.value)}}/>
                </FormControl>
                <FormControl fullWidth variant="standard">
                  <InputLabel htmlFor="component-simple">Age</InputLabel>
                  <Input type="text" placeholder="Age" id="age" value={user.age} onChange={(e) => {setAge(e.target.value)}}/>
                </FormControl>
                <FormControl fullWidth variant="standard" >
                  <InputLabel htmlFor="component-simple">Position</InputLabel>
                  <Input type="text" placeholder="Position" id="position" value={user.position} onChange={(e) => {setPosition(e.target.value)}}/>
                </FormControl>
                <Button variant="contained" color="success" onClick={editUser} style={{marginTop:40}}>
                    Save
                </Button>
              </Box>
            </Grid>
          </Grid>
        </Container>
    )
}

export default UserEdit;
