import React,{useEffect,useState} from "react";
import {useParams} from "react-router-dom";
import Grid from '@mui/material/Grid';
import { Container } from "@mui/system";
import { Input,InputLabel } from "@mui/material";
import Box from '@mui/material/Box';
import FormControl from '@mui/material/FormControl';
import Button from '@mui/material/Button';
import Axios from 'axios';

const UserEdit = ({item}) => {
    const [name, setName] = useState("");
    const [age, setAge] = useState("");
    const [position, setPosition] = useState("");

    const [user, setUser] = useState([]);

    const {id} = useParams();

    // useEffect(() => {
    //     getUser()
    //     setTimeout(()=>{
    //         setName(user[0].name)
    //         setAge(user[0].age)
    //         setPosition(user[0].position)
    //     },1000)
    // },[]);

  useEffect(() => {
      UsersAPI()
  },[]);

  const UsersAPI = () =>{
    Axios.get(`http://localhost:8000/`)
      .then(res => {
          const users = res.data;
          setUser(users.filter(res => res.id === parseInt(id)));
          setName(user[0].name)
          console.log(user[0])
      }).catch(err =>{
        console.log(err)
      });
  }
    // const getUser = () =>{
    //   console.log(item)
    //   setUser(item.filter(res => res.id === parseInt(id)))
    //   console.log(user)
    // }

    const editUser = (id) => {
        const user = { name: name, age: age, position: position };
        Axios.put(`http://localhost:8000/updateUser/${id}`, user)
        .then(() => {
            console.log("edit success")
        }).catch(err=>{
            console.log(err)
        });
    }

    const center = {
        alignItem: "center",
        textAlign: "center"
    }

    return(
        <Container>
          <Grid container >
            <Grid item xs={12} md={12}  style={center} >
              <Box
                component="form"
                noValidate
                autoComplete="off"
                className="box"
              >
                <h1>Edit User</h1>
                <FormControl fullWidth variant="standard" >
                  <InputLabel htmlFor="component-simple">Name</InputLabel>
                  <Input type="text" placeholder="Name" id="name" value={name}  onChange={(e) => {setName(e.target.value)}}/>
                </FormControl>
                <FormControl fullWidth variant="standard">
                  <InputLabel htmlFor="component-simple">Age</InputLabel>
                  <Input type="text" placeholder="Age" id="age" value={age} onChange={(e) => {setAge(e.target.value)}}/>
                </FormControl>
                <FormControl fullWidth variant="standard" >
                  <InputLabel htmlFor="component-simple">Position</InputLabel>
                  <Input type="text" placeholder="Position" id="position" value={position} onChange={(e) => {setPosition(e.target.value)}}/>
                </FormControl>
                
                <Button variant="contained" color="success" onClick={editUser} style={{marginTop:40}}>
                    Save
                </Button>
              </Box>
            </Grid>
          </Grid>
        </Container>
    )
}

export default UserEdit;
