import React, { Component,useState} from "react";
import Grid from '@mui/material/Grid';
import { Container } from "@mui/system";
import { Input } from "@mui/material";
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';
import Button from '@mui/material/Button';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';


const App = ()=> {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [position, setPosition] = useState("");

  const [user,setUser] = useState([]);
  const addUser = () => {
    // setUser({
    //   name : name,
    //   age : age,
    //   position : position
    // })
    setUser([
      ...user,
      {
        name : name,
        age : age,
        position : position
      }
    ])
  }


  const center = {
    alignItem: "center",
    textAlign: "center"
  }
  return (
    <div className="App">
      <Container>
        <Grid container spacing={0}>
          <Grid item xs={12} md={6}  style={center}>
            <h1>Product</h1>
            <Box
              component="form"
              noValidate
              autoComplete="off"
            >
              <FormControl fullWidth variant="standard" >
                {/* <InputLabel htmlFor="component-simple">Name</InputLabel> */}
                <Input type="text" placeholder="Name" id="name" onChange={(e) => {setName(e.target.value)}}/>
              </FormControl>
              <FormControl fullWidth variant="standard">
                {/* <InputLabel htmlFor="component-simple">Age</InputLabel> */}
                <Input type="text" placeholder="Age" id="age" onChange={(e) => {setAge(e.target.value)}}/>
              </FormControl>
              <FormControl fullWidth variant="standard" >
                {/* <InputLabel htmlFor="component-simple">Position</InputLabel> */}
                <Input type="text" placeholder="Position" id="position" onChange={(e) => {setPosition(e.target.value)}}/>
              </FormControl>
              <div margin={10} >
                <Button variant="contained" color="success" onClick={addUser}>
                    Add
                </Button>
              </div>
            </Box>
          </Grid>
          <Grid item xs={12} md={6}  style={center}>
            <Card sx={{ maxWidth: 345 }} variant="outlined" className="card-todoLis">
            <CardMedia
                component="img"
                image={props.image}
                alt={props.name}
            />
            <CardContent>
                <Typography gutterBottom variant="h5" component="div">
                {props.name}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                ${props.price}
                </Typography>
            </CardContent>
        </Card>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}

export default App;
