  return (
    <div className="App">
      <Container>
        <Grid container spacing={0} marginTop={5} >
          <Grid item xs={12} md={3}  style={center}>
            <h1>Product</h1>
            <Box
              component="form"
              noValidate
              autoComplete="off"
            >
              <FormControl fullWidth variant="standard"  marginTop={5} >
                {/* <InputLabel htmlFor="component-simple">Name</InputLabel> */}
                <Input type="text" placeholder="Name" id="name" onChange={(e) => {setName(e.target.value)}}/>
