                <Input type="text" placeholder="Position" id="position" onChange={(e) => {setPosition(e.target.value)}}/>
              </FormControl>
              <div margin={10} >
                <Button variant="contained" color="success" >
                    Add
                </Button>
              </div>
            </Box>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}

export default App;
