import React, {useState} from "react";
import Grid from '@mui/material/Grid';
import { Container } from "@mui/system";
import { Input,InputLabel } from "@mui/material";
import Box from '@mui/material/Box';
import FormControl from '@mui/material/FormControl';
import Button from '@mui/material/Button';
import UserList from './Components/UserList';

const App = ()=> {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [position, setPosition] = useState("");

  const [user,setUser] = useState([]);
  const addUser = () => {
    setUser([
      ...user,
      {
        name : name,
        age : age,
        position : position
      }
    ])
    console.log(user.name)
  }

  const center = {
    alignItem: "center",
    textAlign: "center"
  }

  return (
    <div className="App">
      <Container>
        <Grid container spacing={5}>
          <Grid item xs={12} md={6}  style={center}>
            <h1>Product</h1>
            <Box
              component="form"
              noValidate
              autoComplete="off"
            >
              <FormControl fullWidth variant="standard" >
                <InputLabel htmlFor="component-simple">Name</InputLabel>
                <Input type="text" placeholder="Name" id="name"  onChange={(e) => {setName(e.target.value)}}/>
              </FormControl>
              <FormControl fullWidth variant="standard">
                <InputLabel htmlFor="component-simple">Age</InputLabel>
                <Input type="text" placeholder="Age" id="age" onChange={(e) => {setAge(e.target.value)}}/>
              </FormControl>
              <FormControl fullWidth variant="standard" >
                <InputLabel htmlFor="component-simple">Position</InputLabel>
                <Input type="text" placeholder="Position" id="position" onChange={(e) => {setPosition(e.target.value)}}/>
              </FormControl>
              <Button variant="contained" color="success" onClick={addUser}>
                  Add
              </Button>
            </Box>
          </Grid>
          <Grid item xs={12} md={6} >
            Infomation
            {
              user.map((res,index) => {
                return(
                  <UserList key={index} name={res.name} age={res.age} position={res.position} />
                )
              })
            }
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}

export default App;
